733→  function getTargetPanel() {
734→    const selected = document.querySelector('input[name="targetPanel"]:checked');
735→    return selected ? selected.value : "left";
736→  }
737→
738→  function loadInPanel(panel, path) {
739→    if (!path) return;
740→    const iframe = document.getElementById(`iframe-${panel}`);
741→    if (!iframe) return;
742→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
743→    iframe.src = url;
744→    const inputBox = document.querySelector(`#panel-${panel} input`);
745→    if (inputBox) inputBox.value = path;
746→    console.log(`Loaded "${url}" into panel "${panel}"`);
747→  }
