749→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
750→    link.addEventListener("click", function (e) {
751→      e.preventDefault();
752→      const path = this.getAttribute("href");
753→      loadInPanel(getTargetPanel(), path);
754→    });
755→  });
756→
757→  const idForm = document.getElementById("idsearchForm");
758→  if (idForm) {
759→    idForm.addEventListener("submit", function (e) {
