766→      if (/^\d+$/.test(input)) {
767→        path = "/go_to/" + encodeURIComponent(input);
768→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
769→        const parts = input.split(/\s+/).filter(Boolean);
770→        path = "/range/" + parts.map(encodeURIComponent).join("/");
771→      } else {
772→        alert("Invalid input format.");
773→        return;
774→      }
775→
776→      loadInPanel(getTargetPanel(), path);
777→    });
778→  }
779→
780→  const searchForm = document.getElementById("searchForm");
781→  if (searchForm) {
782→    searchForm.addEventListener("submit", function (e) {
783→      e.preventDefault();
784→      const input = searchForm.querySelector(".searchTerm").value.trim();
785→      if (!input) return;
786→
787→      const path = "/search/" + encodeURIComponent(input);
788→      loadInPanel(getTargetPanel(), path);
789→    });
790→  }
791→});
