645→  }
646→
647→  function renderPreview(textarea, preview, label) {
648→    let raw = textarea.value;
649→    const anchors = [];
650→    let idx = 0;
651→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
652→      (match, qHref, labelText) => {
653→        const href = qHref.slice(1, -1);
654→        const token = `__A_TAG_${idx++}__`;
655→        anchors.push({ token, href: normalizeHref(href), label: labelText });
656→        return token;
657→      });
658→    let safe = escapeHtml(raw);
659→    anchors.forEach(a => {
660→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
661→      safe = safe.replace(a.token, html);
662→    });
663→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
664→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
665→    );
666→    safe = safe.replace(/\n/g, "<br>");
667→    if (!safe.trim()) {
668→      preview.classList.add("hidden");
669→      label.classList.add("hidden");
670→      return;
671→    }
672→    preview.innerHTML = safe;
673→    preview.classList.remove("hidden");
674→    label.classList.remove("hidden");
675→    if (window.MathJax && MathJax.Hub) {
676→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
677→    }
678→  }
679→  document.querySelectorAll("form textarea").forEach(textarea => {
680→    const form = textarea.closest("form");
681→    const preview = form && form.querySelector(".post-preview");
682→    const label = form && form.querySelector(".preview-label");
683→    if (preview && label) {
684→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
685→      renderPreview(textarea, preview, label);
686→    }
687→  });
688→});
689→</script>
