750→  function loadInPanel(panel, path) {
751→    if (!path) return;
752→    const iframe = document.getElementById(`iframe-${panel}`);
753→    if (!iframe) return;
754→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
