761→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
762→    link.addEventListener("click", function (e) {
763→      e.preventDefault();
764→      const path = this.getAttribute("href");
765→      loadInPanel(getTargetPanel(), path);
766→    });
767→  });
768→
769→  const idForm = document.getElementById("idsearchForm");
