781→        const parts = input.split(/\s+/).filter(Boolean);
782→        path = "/range/" + parts.map(encodeURIComponent).join("/");
783→      } else {
784→        alert("Invalid input format.");
785→        return;
786→      }
787→
788→      loadInPanel(getTargetPanel(), path);
789→    });
790→  }
791→
792→  const searchForm = document.getElementById("searchForm");
793→  if (searchForm) {
794→    searchForm.addEventListener("submit", function (e) {
795→      e.preventDefault();
796→      const input = searchForm.querySelector(".searchTerm").value.trim();
797→      if (!input) return;
798→
799→      const path = "/search/" + encodeURIComponent(input);
800→      loadInPanel(getTargetPanel(), path);
801→    });
802→  }
803→});
