150→  form.onsubmit = function() {
151→    var input = document.querySelector('.idsearchTerm').value.trim();
152→    var searchUrl;
153→
154→    if (/^\d+$/.test(input)) {
155→        searchUrl = '/' + encodeURIComponent(input);
156→    } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/.test(input.toLowerCase())) {
157→        let parts = input.split(/\s+/).filter(Boolean);
158→        searchUrl = '/range/' + parts.map(encodeURIComponent).join('/');
159→    } else {
