686→<!-- To handle the search bar -->
687→<script>
688→document.addEventListener('DOMContentLoaded', function () {
689→    function setFilterLink(linkId, newFilter) {
690→        const link = document.getElementById(linkId);
691→        if (!link) return;
692→        const pathParts = window.location.pathname.split("/");
693→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
694→        if (pathParts.length >= 3) {
695→            const section = pathParts[1];
696→            const entry = pathParts[2];
697→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
698→            const baseUrl = "/" + section + "/" + entry;
699→            link.href = baseUrl + "/" + newFilter;
700→        }
701→    }
702→    setFilterLink("solvedLink", "solved");
703→    setFilterLink("openLink", "open");
704→    setFilterLink("yesLink", "yes");
705→    setFilterLink("noLink", "no");
706→});
