709→<!-- Handle the dual view link -->
710→<script>
711→document.addEventListener("DOMContentLoaded", function () {
712→  const dualLink = document.getElementById("dualViewLink");
713→  if (!dualLink) return;
714→
715→  const isInDualView = window.location.pathname.startsWith("/dual");
716→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
717→  dualLink.addEventListener("click", function (e) {
718→    e.preventDefault();
719→
720→    if (isInDualView) {
721→      try {
722→        const leftIframe = document.getElementById("iframe-left");
723→        const src = leftIframe?.src || "/";
724→        let clean = src.replace(window.location.origin, "");
725→        clean = clean.replace(/[?&]embed=1/, "");
726→
727→        window.location.href = clean;
728→      } catch (err) {
729→        window.location.href = "/";
730→      }
731→    } else {
732→      const currentPath = window.location.pathname + window.location.search;
733→      const rightPanelDefault = "/";
734→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
735→      window.location.href = `/dual${hash}`;
736→    }
737→  });
738→});
739→</script>
