741→<!-- Redirect links and searches when in dual view mode -->
742→<script>
743→document.addEventListener("DOMContentLoaded", function () {
744→  if (!window.location.pathname.startsWith("/dual")) return;
745→
746→  function getTargetPanel() {
747→    const selected = document.querySelector('input[name="targetPanel"]:checked');
748→    return selected ? selected.value : "left";
749→  }
750→
751→  function loadInPanel(panel, path) {
752→    if (!path) return;
753→    const iframe = document.getElementById(`iframe-${panel}`);
754→    if (!iframe) return;
755→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
756→    iframe.src = url;
757→    const inputBox = document.querySelector(`#panel-${panel} input`);
758→    if (inputBox) inputBox.value = path;
759→    console.log(`Loaded "${url}" into panel "${panel}"`);
760→  }
761→  
762→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
763→    link.addEventListener("click", function (e) {
764→      e.preventDefault();
765→      const path = this.getAttribute("href");
766→      loadInPanel(getTargetPanel(), path);
767→    });
768→  });
769→
770→  const idForm = document.getElementById("idsearchForm");
771→  if (idForm) {
772→    idForm.addEventListener("submit", function (e) {
773→      e.preventDefault();
774→      const input = idForm.querySelector(".idsearchTerm").value.trim();
775→      if (!input) return;
776→
777→      let path = null;
778→
779→      if (/^\d+$/.test(input)) {
780→        path = "/go_to/" + encodeURIComponent(input);
781→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
782→        const parts = input.split(/\s+/).filter(Boolean);
783→        path = "/range/" + parts.map(encodeURIComponent).join("/");
784→      } else {
785→        alert("Invalid input format.");
786→        return;
787→      }
788→
789→      loadInPanel(getTargetPanel(), path);
790→    });
791→  }
792→
793→  const searchForm = document.getElementById("searchForm");
794→  if (searchForm) {
795→    searchForm.addEventListener("submit", function (e) {
796→      e.preventDefault();
797→      const input = searchForm.querySelector(".searchTerm").value.trim();
798→      if (!input) return;
799→
800→      const path = "/search/" + encodeURIComponent(input);
801→      loadInPanel(getTargetPanel(), path);
802→    });
803→  }
804→});
805→</script>
