648→<!-- Previewing forum posts -->
649→<script>
650→document.addEventListener("DOMContentLoaded", function () {
651→  function escapeHtml(str) {
652→    return str.replace(/[&<>"']/g, m => ({
653→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
654→    })[m]);
655→  }
656→  function normalizeHref(href){
657→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
658→      href = 'https://' + href;
659→    }
660→    return href;
661→  }
662→
663→  function renderPreview(textarea, preview, label) {
664→    let raw = textarea.value;
665→    const anchors = [];
666→    let idx = 0;
667→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
668→      (match, qHref, labelText) => {
669→        const href = qHref.slice(1, -1);
670→        const token = `__A_TAG_${idx++}__`;
671→        anchors.push({ token, href: normalizeHref(href), label: labelText });
672→        return token;
673→      });
674→    let safe = escapeHtml(raw);
675→    anchors.forEach(a => {
676→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
677→      safe = safe.replace(a.token, html);
678→    });
679→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
680→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
681→    );
682→    safe = safe.replace(/\n/g, "<br>");
683→    if (!safe.trim()) {
684→      preview.classList.add("hidden");
685→      label.classList.add("hidden");
686→      return;
687→    }
688→    preview.innerHTML = safe;
689→    preview.classList.remove("hidden");
690→    label.classList.remove("hidden");
691→    if (window.MathJax && MathJax.Hub) {
692→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
693→    }
694→  }
695→  document.querySelectorAll("form textarea").forEach(textarea => {
696→    const form = textarea.closest("form");
697→    const preview = form && form.querySelector(".post-preview");
698→    const label = form && form.querySelector(".preview-label");
699→    if (preview && label) {
700→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
701→      renderPreview(textarea, preview, label);
702→    }
703→  });
704→});
705→</script>
