647→<!-- Previewing forum posts -->
648→<script>
649→document.addEventListener("DOMContentLoaded", function () {
650→  function escapeHtml(str) {
651→    return str.replace(/[&<>"']/g, m => ({
652→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
653→    })[m]);
654→  }
655→  function normalizeHref(href){
656→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
657→      href = 'https://' + href;
658→    }
659→    return href;
660→  }
661→
662→  function renderPreview(textarea, preview, label) {
663→    let raw = textarea.value;
664→    const anchors = [];
665→    let idx = 0;
666→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
667→      (match, qHref, labelText) => {
668→        const href = qHref.slice(1, -1);
669→        const token = `__A_TAG_${idx++}__`;
670→        anchors.push({ token, href: normalizeHref(href), label: labelText });
671→        return token;
672→      });
673→    let safe = escapeHtml(raw);
674→    anchors.forEach(a => {
675→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
676→      safe = safe.replace(a.token, html);
677→    });
678→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
679→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
680→    );
681→    safe = safe.replace(/\n/g, "<br>");
682→    if (!safe.trim()) {
683→      preview.classList.add("hidden");
684→      label.classList.add("hidden");
685→      return;
686→    }
687→    preview.innerHTML = safe;
688→    preview.classList.remove("hidden");
689→    label.classList.remove("hidden");
690→    if (window.MathJax && MathJax.Hub) {
691→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
692→    }
693→  }
694→  document.querySelectorAll("form textarea").forEach(textarea => {
695→    const form = textarea.closest("form");
696→    const preview = form && form.querySelector(".post-preview");
697→    const label = form && form.querySelector(".preview-label");
698→    if (preview && label) {
699→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
700→      renderPreview(textarea, preview, label);
701→    }
702→  });
703→});
704→</script>
