 15→<script>
 16→document.addEventListener('click', (e) => {
 17→  // Toggle reply form
 18→  const toggle = e.target.closest('.reply-toggle');
 19→  if (toggle) {
 20→    const id = toggle.getAttribute('data-target');
 21→    const form = document.getElementById(id);
 22→    if (form) form.classList.toggle('hidden');
 23→  }
 24→  // Reply with quote
 25→  const quoteBtn = e.target.closest('.reply-quote');
 26→  if (quoteBtn) {
 27→    const id = quoteBtn.getAttribute('data-target');
 28→    const form = document.getElementById(id);
 29→    if (!form) return;
 30→    const ta = form.querySelector('textarea');
 31→    const raw = quoteBtn.getAttribute('data-raw') || '';
 32→    const quoted = raw.split('\n').map(l => '> ' + l).join('\n') + '\n\n';
 33→    ta.value = quoted;
 34→    form.classList.remove('hidden');
 35→    ta.focus();
 36→    const len = ta.value.length;
 37→    ta.setSelectionRange(len, len);
 38→  }
 39→});
 40→</script>
