705→<!-- Handle the dual view link -->
706→<script>
707→document.addEventListener("DOMContentLoaded", function () {
708→  const dualLink = document.getElementById("dualViewLink");
709→  if (!dualLink) return;
710→
711→  const isInDualView = window.location.pathname.startsWith("/dual");
712→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
713→  dualLink.addEventListener("click", function (e) {
714→    e.preventDefault();
715→
716→    if (isInDualView) {
717→      try {
718→        const leftIframe = document.getElementById("iframe-left");
719→        const src = leftIframe?.src || "/";
720→        let clean = src.replace(window.location.origin, "");
721→        clean = clean.replace(/[?&]embed=1/, "");
722→
723→        window.location.href = clean;
724→      } catch (err) {
725→        window.location.href = "/";
726→      }
727→    } else {
728→      const currentPath = window.location.pathname + window.location.search;
729→      const rightPanelDefault = "/";
730→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
731→      window.location.href = `/dual${hash}`;
732→    }
733→  });
734→});
735→</script>
