737→<!-- Redirect links and searches when in dual view mode -->
738→<script>
739→document.addEventListener("DOMContentLoaded", function () {
740→  if (!window.location.pathname.startsWith("/dual")) return;
741→
742→  function getTargetPanel() {
743→    const selected = document.querySelector('input[name="targetPanel"]:checked');
744→    return selected ? selected.value : "left";
745→  }
746→
747→  function loadInPanel(panel, path) {
748→    if (!path) return;
749→    const iframe = document.getElementById(`iframe-${panel}`);
750→    if (!iframe) return;
751→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
752→    iframe.src = url;
753→    const inputBox = document.querySelector(`#panel-${panel} input`);
754→    if (inputBox) inputBox.value = path;
755→    console.log(`Loaded "${url}" into panel "${panel}"`);
756→  }
757→  
758→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
759→    link.addEventListener("click", function (e) {
760→      e.preventDefault();
761→      const path = this.getAttribute("href");
762→      loadInPanel(getTargetPanel(), path);
763→    });
764→  });
765→
766→  const idForm = document.getElementById("idsearchForm");
767→  if (idForm) {
768→    idForm.addEventListener("submit", function (e) {
769→      e.preventDefault();
770→      const input = idForm.querySelector(".idsearchTerm").value.trim();
771→      if (!input) return;
772→
773→      let path = null;
774→
775→      if (/^\d+$/.test(input)) {
776→        path = "/go_to/" + encodeURIComponent(input);
777→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
778→        const parts = input.split(/\s+/).filter(Boolean);
779→        path = "/range/" + parts.map(encodeURIComponent).join("/");
780→      } else {
781→        alert("Invalid input format.");
782→        return;
783→      }
784→
785→      loadInPanel(getTargetPanel(), path);
786→    });
787→  }
788→
789→  const searchForm = document.getElementById("searchForm");
790→  if (searchForm) {
791→    searchForm.addEventListener("submit", function (e) {
792→      e.preventDefault();
793→      const input = searchForm.querySelector(".searchTerm").value.trim();
794→      if (!input) return;
795→
796→      const path = "/search/" + encodeURIComponent(input);
797→      loadInPanel(getTargetPanel(), path);
798→    });
799→  }
800→});
801→</script>
