631→    return str.replace(/[&<>"']/g, m => ({
632→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
633→    })[m]);
634→  }
635→  function normalizeHref(href){
636→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
637→      href = 'https://' + href;
638→    }
639→    return href;
640→  }
641→
642→  function renderPreview(textarea, preview, label) {
643→    let raw = textarea.value;
644→    const anchors = [];
645→    let idx = 0;
646→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
647→      (match, qHref, labelText) => {
648→        const href = qHref.slice(1, -1);
649→        const token = `__A_TAG_${idx++}__`;
650→        anchors.push({ token, href: normalizeHref(href), label: labelText });
651→        return token;
652→      });
653→    let safe = escapeHtml(raw);
654→    anchors.forEach(a => {
655→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
656→      safe = safe.replace(a.token, html);
657→    });
658→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
659→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
660→    );
661→    safe = safe.replace(/\n/g, "<br>");
662→    if (!safe.trim()) {
663→      preview.classList.add("hidden");
664→      label.classList.add("hidden");
665→      return;
666→    }
667→    preview.innerHTML = safe;
668→    preview.classList.remove("hidden");
669→    label.classList.remove("hidden");
670→    if (window.MathJax && MathJax.Hub) {
671→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
672→    }
673→  }
674→  document.querySelectorAll("form textarea").forEach(textarea => {
675→    const form = textarea.closest("form");
676→    const preview = form && form.querySelector(".post-preview");
677→    const label = form && form.querySelector(".preview-label");
678→    if (preview && label) {
679→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
680→      renderPreview(textarea, preview, label);
681→    }
682→  });
683→});
684→</script>
