125→      const panel = selected ? selected.value : "left";
126→      const iframe = document.getElementById(`iframe-${panel}`);
127→      if (iframe) {
128→        const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
129→        iframe.src = url;
130→        const box = document.querySelector(`#panel-${panel} input`);
131→        if (box) box.value = path;
132→      } else {
133→        window.location.href = path;
134→      }
135→    } else {
136→      form.action = path;
137→      form.method = "GET";
138→    }
139→  });
140→});
