694→<!-- To handle the search bar -->
695→<script>
696→document.addEventListener('DOMContentLoaded', function () {
697→    function setFilterLink(linkId, newFilter) {
698→        const link = document.getElementById(linkId);
699→        if (!link) return;
700→        const pathParts = window.location.pathname.split("/");
701→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
702→        if (pathParts.length >= 3) {
703→            const section = pathParts[1];
704→            const entry = pathParts[2];
705→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
706→            const baseUrl = "/" + section + "/" + entry;
707→            link.href = baseUrl + "/" + newFilter;
708→        }
709→    }
710→    setFilterLink("solvedLink", "solved");
711→    setFilterLink("openLink", "open");
712→    setFilterLink("yesLink", "yes");
713→    setFilterLink("noLink", "no");
714→});
