717→<!-- Handle the dual view link -->
718→<script>
719→document.addEventListener("DOMContentLoaded", function () {
720→  const dualLink = document.getElementById("dualViewLink");
721→  if (!dualLink) return;
722→
723→  const isInDualView = window.location.pathname.startsWith("/dual");
724→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
725→  dualLink.addEventListener("click", function (e) {
726→    e.preventDefault();
727→
728→    if (isInDualView) {
729→      try {
730→        const leftIframe = document.getElementById("iframe-left");
731→        const src = leftIframe?.src || "/";
732→        let clean = src.replace(window.location.origin, "");
733→        clean = clean.replace(/[?&]embed=1/, "");
734→
735→        window.location.href = clean;
736→      } catch (err) {
737→        window.location.href = "/";
738→      }
739→    } else {
740→      const currentPath = window.location.pathname + window.location.search;
741→      const rightPanelDefault = "/";
742→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
743→      window.location.href = `/dual${hash}`;
744→    }
745→  });
746→});
747→</script>
