749→<!-- Redirect links and searches when in dual view mode -->
750→<script>
751→document.addEventListener("DOMContentLoaded", function () {
752→  if (!window.location.pathname.startsWith("/dual")) return;
753→
754→  function getTargetPanel() {
755→    const selected = document.querySelector('input[name="targetPanel"]:checked');
756→    return selected ? selected.value : "left";
757→  }
758→
759→  function loadInPanel(panel, path) {
760→    if (!path) return;
761→    const iframe = document.getElementById(`iframe-${panel}`);
762→    if (!iframe) return;
763→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
764→    iframe.src = url;
765→    const inputBox = document.querySelector(`#panel-${panel} input`);
766→    if (inputBox) inputBox.value = path;
767→    console.log(`Loaded "${url}" into panel "${panel}"`);
768→  }
769→  
770→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
771→    link.addEventListener("click", function (e) {
772→      e.preventDefault();
773→      const path = this.getAttribute("href");
774→      loadInPanel(getTargetPanel(), path);
775→    });
776→  });
777→
778→  const idForm = document.getElementById("idsearchForm");
779→  if (idForm) {
780→    idForm.addEventListener("submit", function (e) {
781→      e.preventDefault();
782→      const input = idForm.querySelector(".idsearchTerm").value.trim();
783→      if (!input) return;
784→
785→      let path = null;
786→
787→      if (/^\d+$/.test(input)) {
788→        path = "/go_to/" + encodeURIComponent(input);
789→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
790→        const parts = input.split(/\s+/).filter(Boolean);
791→        path = "/range/" + parts.map(encodeURIComponent).join("/");
792→      } else {
793→        alert("Invalid input format.");
794→        return;
795→      }
796→
797→      loadInPanel(getTargetPanel(), path);
798→    });
799→  }
800→
801→  const searchForm = document.getElementById("searchForm");
802→  if (searchForm) {
803→    searchForm.addEventListener("submit", function (e) {
804→      e.preventDefault();
805→      const input = searchForm.querySelector(".searchTerm").value.trim();
806→      if (!input) return;
807→
808→      const path = "/search/" + encodeURIComponent(input);
809→      loadInPanel(getTargetPanel(), path);
810→    });
811→  }
812→});
813→</script>
