620→<script>
621→document.addEventListener("DOMContentLoaded", function () {
622→  function escapeHtml(str) {
623→    return str.replace(/[&<>"']/g, m => ({
624→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
625→    })[m]);
626→  }
627→  function normalizeHref(href){
628→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
629→      href = 'https://' + href;
630→    }
631→    return href;
632→  }
633→
634→  function renderPreview(textarea, preview, label) {
635→    let raw = textarea.value;
636→    const anchors = [];
637→    let idx = 0;
638→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
639→      (match, qHref, labelText) => {
640→        const href = qHref.slice(1, -1);
641→        const token = `__A_TAG_${idx++}__`;
642→        anchors.push({ token, href: normalizeHref(href), label: labelText });
643→        return token;
644→      });
645→    let safe = escapeHtml(raw);
646→    anchors.forEach(a => {
647→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
648→      safe = safe.replace(a.token, html);
649→    });
650→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
651→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
652→    );
653→    safe = safe.replace(/\n/g, "<br>");
654→    if (!safe.trim()) {
655→      preview.classList.add("hidden");
656→      label.classList.add("hidden");
657→      return;
658→    }
659→    preview.innerHTML = safe;
660→    preview.classList.remove("hidden");
661→    label.classList.remove("hidden");
662→    if (window.MathJax && MathJax.Hub) {
663→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
664→    }
665→  }
666→  document.querySelectorAll("form textarea").forEach(textarea => {
667→    const form = textarea.closest("form");
668→    const preview = form && form.querySelector(".post-preview");
669→    const label = form && form.querySelector(".preview-label");
670→    if (preview && label) {
671→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
672→      renderPreview(textarea, preview, label);
673→    }
674→  });
675→});
