737→
738→  function getTargetPanel() {
739→    const selected = document.querySelector('input[name="targetPanel"]:checked');
740→    return selected ? selected.value : "left";
741→  }
742→
743→  function loadInPanel(panel, path) {
744→    if (!path) return;
745→    const iframe = document.getElementById(`iframe-${panel}`);
746→    if (!iframe) return;
747→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
748→    iframe.src = url;
749→    const inputBox = document.querySelector(`#panel-${panel} input`);
750→    if (inputBox) inputBox.value = path;
751→    console.log(`Loaded "${url}" into panel "${panel}"`);
752→  }
