754→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
755→    link.addEventListener("click", function (e) {
756→      e.preventDefault();
757→      const path = this.getAttribute("href");
758→      loadInPanel(getTargetPanel(), path);
759→    });
760→  });
761→
762→  const idForm = document.getElementById("idsearchForm");
