768→
769→      let path = null;
770→
771→      if (/^\d+$/.test(input)) {
772→        path = "/go_to/" + encodeURIComponent(input);
773→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
774→        const parts = input.split(/\s+/).filter(Boolean);
775→        path = "/range/" + parts.map(encodeURIComponent).join("/");
776→      } else {
777→        alert("Invalid input format.");
778→        return;
779→      }
780→
781→      loadInPanel(getTargetPanel(), path);
782→    });
783→  }
784→
785→  const searchForm = document.getElementById("searchForm");
786→  if (searchForm) {
787→    searchForm.addEventListener("submit", function (e) {
788→      e.preventDefault();
789→      const input = searchForm.querySelector(".searchTerm").value.trim();
790→      if (!input) return;
791→
792→      const path = "/search/" + encodeURIComponent(input);
793→      loadInPanel(getTargetPanel(), path);
794→    });
795→  }
796→});
