 44→<script>
 45→function addNewBox(key, problem_id) {
 46→  const local_id = 'bib-container' + String(problem_id);
 47→  const container = document.getElementById(local_id);
 48→  if (!container) return;
 49→
 50→  container.innerHTML = "";
 51→
 52→  fetch(`/bibs/${encodeURIComponent(key)}`)
 53→    .then(res => {
 54→      if (!res.ok) throw new Error(`HTTP ${res.status}`);
 55→      return res.text();
 56→    })
 57→    .then(html => {
 58→      container.innerHTML = html;
 59→    })
 60→    .catch(err => {
 61→      console.error("Failed to load bib:", err);
 62→      container.textContent = "Unable to load reference.";
 63→    });
 64→}
 65→</script>
