700→<!-- Handle the dual view link -->
701→<script>
702→document.addEventListener("DOMContentLoaded", function () {
703→  const dualLink = document.getElementById("dualViewLink");
704→  if (!dualLink) return;
705→
706→  const isInDualView = window.location.pathname.startsWith("/dual");
707→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
708→  dualLink.addEventListener("click", function (e) {
709→    e.preventDefault();
710→
711→    if (isInDualView) {
712→      try {
713→        const leftIframe = document.getElementById("iframe-left");
714→        const src = leftIframe?.src || "/";
715→        let clean = src.replace(window.location.origin, "");
716→        clean = clean.replace(/[?&]embed=1/, "");
717→
718→        window.location.href = clean;
719→      } catch (err) {
720→        window.location.href = "/";
721→      }
722→    } else {
723→      const currentPath = window.location.pathname + window.location.search;
724→      const rightPanelDefault = "/";
725→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
726→      window.location.href = `/dual${hash}`;
727→    }
728→  });
729→});
730→</script>
