732→<!-- Redirect links and searches when in dual view mode -->
733→<script>
734→document.addEventListener("DOMContentLoaded", function () {
735→  if (!window.location.pathname.startsWith("/dual")) return;
736→
737→  function getTargetPanel() {
738→    const selected = document.querySelector('input[name="targetPanel"]:checked');
739→    return selected ? selected.value : "left";
740→  }
741→
742→  function loadInPanel(panel, path) {
743→    if (!path) return;
744→    const iframe = document.getElementById(`iframe-${panel}`);
745→    if (!iframe) return;
746→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
747→    iframe.src = url;
748→    const inputBox = document.querySelector(`#panel-${panel} input`);
749→    if (inputBox) inputBox.value = path;
750→    console.log(`Loaded "${url}" into panel "${panel}"`);
751→  }
752→  
753→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
754→    link.addEventListener("click", function (e) {
755→      e.preventDefault();
756→      const path = this.getAttribute("href");
757→      loadInPanel(getTargetPanel(), path);
758→    });
759→  });
760→
761→  const idForm = document.getElementById("idsearchForm");
762→  if (idForm) {
763→    idForm.addEventListener("submit", function (e) {
764→      e.preventDefault();
765→      const input = idForm.querySelector(".idsearchTerm").value.trim();
766→      if (!input) return;
767→
768→      let path = null;
769→
770→      if (/^\d+$/.test(input)) {
771→        path = "/go_to/" + encodeURIComponent(input);
772→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
773→        const parts = input.split(/\s+/).filter(Boolean);
774→        path = "/range/" + parts.map(encodeURIComponent).join("/");
775→      } else {
776→        alert("Invalid input format.");
777→        return;
778→      }
779→
780→      loadInPanel(getTargetPanel(), path);
781→    });
782→  }
783→
784→  const searchForm = document.getElementById("searchForm");
785→  if (searchForm) {
786→    searchForm.addEventListener("submit", function (e) {
787→      e.preventDefault();
788→      const input = searchForm.querySelector(".searchTerm").value.trim();
789→      if (!input) return;
790→
791→      const path = "/search/" + encodeURIComponent(input);
792→      loadInPanel(getTargetPanel(), path);
793→    });
794→  }
795→});
796→</script>
