111→<script>
112→document.addEventListener('DOMContentLoaded', function() {
113→  const form = document.getElementById('searchForm');
114→  if (!form) return;
115→  form.addEventListener('submit', function (e) {
116→    const input = form.querySelector('.searchTerm');
117→    if (!input) return;
118→    const value = input.value.trim();
119→    if (!value) return;
120→    const path = '/search/' + encodeURIComponent(value);
121→    const inDual = window.location.pathname.startsWith("/dual");
122→    if (inDual) {
123→      e.preventDefault(); 
124→      const selected = document.querySelector('input[name="targetPanel"]:checked');
125→      const panel = selected ? selected.value : "left";
126→      const iframe = document.getElementById(`iframe-${panel}`);
127→      if (iframe) {
128→        const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
129→        iframe.src = url;
130→        const box = document.querySelector(`#panel-${panel} input`);
131→        if (box) box.value = path;
132→      } else {
133→        window.location.href = path;
134→      }
135→    } else {
136→      form.action = path;
137→      form.method = "GET";
138→    }
139→  });
140→});
141→</script>
