647→        anchors.push({ token, href: normalizeHref(href), label: labelText });
648→        return token;
649→      });
650→    let safe = escapeHtml(raw);
651→    anchors.forEach(a => {
652→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
653→      safe = safe.replace(a.token, html);
654→    });
655→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
656→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
657→    );
658→    safe = safe.replace(/\n/g, "<br>");
659→    if (!safe.trim()) {
660→      preview.classList.add("hidden");
661→      label.classList.add("hidden");
662→      return;
663→    }
664→    preview.innerHTML = safe;
665→    preview.classList.remove("hidden");
666→    label.classList.remove("hidden");
667→    if (window.MathJax && MathJax.Hub) {
668→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
669→    }
670→  }
671→  document.querySelectorAll("form textarea").forEach(textarea => {
672→    const form = textarea.closest("form");
673→    const preview = form && form.querySelector(".post-preview");
674→    const label = form && form.querySelector(".preview-label");
675→    if (preview && label) {
676→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
677→      renderPreview(textarea, preview, label);
678→    }
679→  });
680→});
