639→<!-- Previewing forum posts -->
640→<script>
641→document.addEventListener("DOMContentLoaded", function () {
642→  function escapeHtml(str) {
643→    return str.replace(/[&<>"']/g, m => ({
644→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
645→    })[m]);
646→  }
647→  function normalizeHref(href){
648→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
649→      href = 'https://' + href;
650→    }
651→    return href;
652→  }
653→
654→  function renderPreview(textarea, preview, label) {
655→    let raw = textarea.value;
656→    const anchors = [];
657→    let idx = 0;
658→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
659→      (match, qHref, labelText) => {
660→        const href = qHref.slice(1, -1);
661→        const token = `__A_TAG_${idx++}__`;
662→        anchors.push({ token, href: normalizeHref(href), label: labelText });
663→        return token;
664→      });
665→    let safe = escapeHtml(raw);
666→    anchors.forEach(a => {
667→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
668→      safe = safe.replace(a.token, html);
669→    });
670→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
671→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
672→    );
673→    safe = safe.replace(/\n/g, "<br>");
674→    if (!safe.trim()) {
675→      preview.classList.add("hidden");
676→      label.classList.add("hidden");
677→      return;
678→    }
679→    preview.innerHTML = safe;
680→    preview.classList.remove("hidden");
681→    label.classList.remove("hidden");
682→    if (window.MathJax && MathJax.Hub) {
683→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
684→    }
685→  }
686→  document.querySelectorAll("form textarea").forEach(textarea => {
687→    const form = textarea.closest("form");
688→    const preview = form && form.querySelector(".post-preview");
689→    const label = form && form.querySelector(".preview-label");
690→    if (preview && label) {
691→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
692→      renderPreview(textarea, preview, label);
693→    }
694→  });
695→});
696→</script>
