753→<!-- Redirect links and searches when in dual view mode -->
754→<script>
755→document.addEventListener("DOMContentLoaded", function () {
756→  if (!window.location.pathname.startsWith("/dual")) return;
757→
758→  function getTargetPanel() {
759→    const selected = document.querySelector('input[name="targetPanel"]:checked');
760→    return selected ? selected.value : "left";
761→  }
762→
763→  function loadInPanel(panel, path) {
764→    if (!path) return;
765→    const iframe = document.getElementById(`iframe-${panel}`);
766→    if (!iframe) return;
767→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
768→    iframe.src = url;
769→    const inputBox = document.querySelector(`#panel-${panel} input`);
770→    if (inputBox) inputBox.value = path;
771→    console.log(`Loaded "${url}" into panel "${panel}"`);
772→  }
773→  
774→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
775→    link.addEventListener("click", function (e) {
776→      e.preventDefault();
777→      const path = this.getAttribute("href");
778→      loadInPanel(getTargetPanel(), path);
779→    });
780→  });
781→
782→  const idForm = document.getElementById("idsearchForm");
783→  if (idForm) {
784→    idForm.addEventListener("submit", function (e) {
785→      e.preventDefault();
786→      const input = idForm.querySelector(".idsearchTerm").value.trim();
787→      if (!input) return;
788→
789→      let path = null;
790→
791→      if (/^\d+$/.test(input)) {
792→        path = "/go_to/" + encodeURIComponent(input);
793→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
794→        const parts = input.split(/\s+/).filter(Boolean);
795→        path = "/range/" + parts.map(encodeURIComponent).join("/");
796→      } else {
797→        alert("Invalid input format.");
798→        return;
799→      }
800→
801→      loadInPanel(getTargetPanel(), path);
802→    });
803→  }
804→
805→  const searchForm = document.getElementById("searchForm");
806→  if (searchForm) {
807→    searchForm.addEventListener("submit", function (e) {
808→      e.preventDefault();
809→      const input = searchForm.querySelector(".searchTerm").value.trim();
810→      if (!input) return;
811→
812→      const path = "/search/" + encodeURIComponent(input);
813→      loadInPanel(getTargetPanel(), path);
814→    });
815→  }
816→});
