645→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
646→      (match, qHref, labelText) => {
647→        const href = qHref.slice(1, -1);
648→        const token = `__A_TAG_${idx++}__`;
649→        anchors.push({ token, href: normalizeHref(href), label: labelText });
650→        return token;
651→      });
652→    let safe = escapeHtml(raw);
653→    anchors.forEach(a => {
654→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
655→      safe = safe.replace(a.token, html);
656→    });
657→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
658→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
659→    );
660→    safe = safe.replace(/\n/g, "<br>");
661→    if (!safe.trim()) {
662→      preview.classList.add("hidden");
663→      label.classList.add("hidden");
664→      return;
665→    }
666→    preview.innerHTML = safe;
667→    preview.classList.remove("hidden");
668→    label.classList.remove("hidden");
669→    if (window.MathJax && MathJax.Hub) {
670→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
671→    }
672→  }
673→  document.querySelectorAll("form textarea").forEach(textarea => {
674→    const form = textarea.closest("form");
675→    const preview = form && form.querySelector(".post-preview");
676→    const label = form && form.querySelector(".preview-label");
677→    if (preview && label) {
678→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
679→      renderPreview(textarea, preview, label);
680→    }
681→  });
682→});
683→</script>
