   174→<script>
   175→document.addEventListener("click", async (ev) => {
   176→  const btn = ev.target.closest(".reaction-btn");
   177→  if (!btn) return;
   178→  const you = window.CURRENT_USERNAME || "";
   179→  const bar = btn.closest(".reaction-bar");
   180→  const postId = bar?.dataset.postId;
   181→  const rtype = btn.dataset.type;
   182→  const reactUrl = bar?.dataset.reactUrl; 
   183→  if (!postId || !rtype || !reactUrl) return;
   184→
   185→  try {
   186→    const res = await fetch(reactUrl, {
   187→      method: "POST",
   188→      credentials: "same-origin",
   189→      headers: {"Content-Type": "application/json"},
   190→      body: JSON.stringify({type: rtype})
   191→    });
   192→    const data = await res.json();
   193→    if (!data.ok) {
   194→      alert(data.error || "Could not update reaction.");
   195→      return;
   196→    }
   197→    const summary = data.summary;
   198→    
   199→    bar.querySelectorAll(".reaction-btn").forEach(b => {
   200→      const t = b.dataset.type;
   201→      const countSpan = bar.querySelector(`.reaction-count[data-type="${t}"]`);
   202→      if (countSpan && summary[t]) {
   203→        countSpan.textContent = summary[t].count;
   204→    const desc = b.dataset.desc || "";
   205→    const usersTitle = summary[t].users_title || "No one yet.";
   206→    b.title = `${desc}\n\n${usersTitle}`;
   207→      }
   208→        const iReacted = you && (summary[t].users_title || "").includes(you);
   209→  b.classList.toggle("active", !!iReacted);
   210→  b.setAttribute("aria-pressed", iReacted ? "true" : "false");
   211→    });
   212→  } catch (e) {
   213→    console.error(e);
   214→    alert("Only registered users with 5+ posts can react.");
   215→  }
   216→});
   217→</script>
