740→<!-- Redirect links and searches when in dual view mode -->
741→<script>
742→document.addEventListener("DOMContentLoaded", function () {
743→  if (!window.location.pathname.startsWith("/dual")) return;
744→
745→  function getTargetPanel() {
746→    const selected = document.querySelector('input[name="targetPanel"]:checked');
747→    return selected ? selected.value : "left";
748→  }
749→
750→  function loadInPanel(panel, path) {
751→    if (!path) return;
752→    const iframe = document.getElementById(`iframe-${panel}`);
753→    if (!iframe) return;
754→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
755→    iframe.src = url;
756→    const inputBox = document.querySelector(`#panel-${panel} input`);
757→    if (inputBox) inputBox.value = path;
758→    console.log(`Loaded "${url}" into panel "${panel}"`);
759→  }
760→  
761→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
762→    link.addEventListener("click", function (e) {
763→      e.preventDefault();
764→      const path = this.getAttribute("href");
765→      loadInPanel(getTargetPanel(), path);
766→    });
767→  });
768→
769→  const idForm = document.getElementById("idsearchForm");
770→  if (idForm) {
771→    idForm.addEventListener("submit", function (e) {
772→      e.preventDefault();
773→      const input = idForm.querySelector(".idsearchTerm").value.trim();
774→      if (!input) return;
775→
776→      let path = null;
777→
778→      if (/^\d+$/.test(input)) {
779→        path = "/go_to/" + encodeURIComponent(input);
780→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
781→        const parts = input.split(/\s+/).filter(Boolean);
782→        path = "/range/" + parts.map(encodeURIComponent).join("/");
783→      } else {
784→        alert("Invalid input format.");
785→        return;
786→      }
787→
788→      loadInPanel(getTargetPanel(), path);
789→    });
790→  }
791→
792→  const searchForm = document.getElementById("searchForm");
793→  if (searchForm) {
794→    searchForm.addEventListener("submit", function (e) {
795→      e.preventDefault();
796→      const input = searchForm.querySelector(".searchTerm").value.trim();
797→      if (!input) return;
798→
799→      const path = "/search/" + encodeURIComponent(input);
800→      loadInPanel(getTargetPanel(), path);
801→    });
802→  }
803→});
804→</script>
