632→document.addEventListener("DOMContentLoaded", function () {
633→  function escapeHtml(str) {
634→    return str.replace(/[&<>"']/g, m => ({
635→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
636→    })[m]);
637→  }
638→  function normalizeHref(href){
639→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
640→      href = 'https://' + href;
641→    }
642→    return href;
643→  }
644→
645→  function renderPreview(textarea, preview, label) {
646→    let raw = textarea.value;
647→    const anchors = [];
648→    let idx = 0;
649→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
650→      (match, qHref, labelText) => {
651→        const href = qHref.slice(1, -1);
652→        const token = `__A_TAG_${idx++}__`;
653→        anchors.push({ token, href: normalizeHref(href), label: labelText });
654→        return token;
655→      });
656→    let safe = escapeHtml(raw);
657→    anchors.forEach(a => {
658→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
659→      safe = safe.replace(a.token, html);
660→    });
661→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
662→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
663→    );
664→    safe = safe.replace(/\n/g, "<br>");
665→    if (!safe.trim()) {
666→      preview.classList.add("hidden");
667→      label.classList.add("hidden");
668→      return;
669→    }
670→    preview.innerHTML = safe;
671→    preview.classList.remove("hidden");
672→    label.classList.remove("hidden");
673→    if (window.MathJax && MathJax.Hub) {
674→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
675→    }
676→  }
677→  document.querySelectorAll("form textarea").forEach(textarea => {
678→    const form = textarea.closest("form");
679→    const preview = form && form.querySelector(".post-preview");
680→    const label = form && form.querySelector(".preview-label");
681→    if (preview && label) {
682→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
683→      renderPreview(textarea, preview, label);
684→    }
685→  });
686→});
687→</script>
