634→<!-- Previewing forum posts -->
635→<script>
636→document.addEventListener("DOMContentLoaded", function () {
637→  function escapeHtml(str) {
638→    return str.replace(/[&<>"']/g, m => ({
639→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
640→    })[m]);
641→  }
642→  function normalizeHref(href){
643→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
644→      href = 'https://' + href;
645→    }
646→    return href;
647→  }
648→
649→  function renderPreview(textarea, preview, label) {
650→    let raw = textarea.value;
651→    const anchors = [];
652→    let idx = 0;
653→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
654→      (match, qHref, labelText) => {
655→        const href = qHref.slice(1, -1);
656→        const token = `__A_TAG_${idx++}__`;
657→        anchors.push({ token, href: normalizeHref(href), label: labelText });
658→        return token;
659→      });
660→    let safe = escapeHtml(raw);
661→    anchors.forEach(a => {
662→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
663→      safe = safe.replace(a.token, html);
664→    });
665→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
666→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
667→    );
668→    safe = safe.replace(/\n/g, "<br>");
669→    if (!safe.trim()) {
670→      preview.classList.add("hidden");
671→      label.classList.add("hidden");
672→      return;
673→    }
674→    preview.innerHTML = safe;
675→    preview.classList.remove("hidden");
676→    label.classList.remove("hidden");
677→    if (window.MathJax && MathJax.Hub) {
678→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
679→    }
680→  }
681→  document.querySelectorAll("form textarea").forEach(textarea => {
682→    const form = textarea.closest("form");
683→    const preview = form && form.querySelector(".post-preview");
684→    const label = form && form.querySelector(".preview-label");
685→    if (preview && label) {
686→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
687→      renderPreview(textarea, preview, label);
688→    }
689→  });
690→});
691→</script>
