693→<!-- To handle the search bar -->
694→<script>
695→document.addEventListener('DOMContentLoaded', function () {
696→    function setFilterLink(linkId, newFilter) {
697→        const link = document.getElementById(linkId);
698→        if (!link) return;
699→        const pathParts = window.location.pathname.split("/");
700→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
701→        if (pathParts.length >= 3) {
702→            const section = pathParts[1];
703→            const entry = pathParts[2];
704→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
705→            const baseUrl = "/" + section + "/" + entry;
706→            link.href = baseUrl + "/" + newFilter;
707→        }
708→    }
709→    setFilterLink("solvedLink", "solved");
710→    setFilterLink("openLink", "open");
711→    setFilterLink("yesLink", "yes");
712→    setFilterLink("noLink", "no");
713→});
714→</script>
