716→<!-- Handle the dual view link -->
717→<script>
718→document.addEventListener("DOMContentLoaded", function () {
719→  const dualLink = document.getElementById("dualViewLink");
720→  if (!dualLink) return;
721→
722→  const isInDualView = window.location.pathname.startsWith("/dual");
723→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
724→  dualLink.addEventListener("click", function (e) {
725→    e.preventDefault();
726→
727→    if (isInDualView) {
728→      try {
729→        const leftIframe = document.getElementById("iframe-left");
730→        const src = leftIframe?.src || "/";
731→        let clean = src.replace(window.location.origin, "");
732→        clean = clean.replace(/[?&]embed=1/, "");
733→
734→        window.location.href = clean;
735→      } catch (err) {
736→        window.location.href = "/";
737→      }
738→    } else {
739→      const currentPath = window.location.pathname + window.location.search;
740→      const rightPanelDefault = "/";
741→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
742→      window.location.href = `/dual${hash}`;
743→    }
744→  });
745→});
746→</script>
