748→<!-- Redirect links and searches when in dual view mode -->
749→<script>
750→document.addEventListener("DOMContentLoaded", function () {
751→  if (!window.location.pathname.startsWith("/dual")) return;
752→
753→  function getTargetPanel() {
754→    const selected = document.querySelector('input[name="targetPanel"]:checked');
755→    return selected ? selected.value : "left";
756→  }
757→
758→  function loadInPanel(panel, path) {
759→    if (!path) return;
760→    const iframe = document.getElementById(`iframe-${panel}`);
761→    if (!iframe) return;
762→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
763→    iframe.src = url;
764→    const inputBox = document.querySelector(`#panel-${panel} input`);
765→    if (inputBox) inputBox.value = path;
766→    console.log(`Loaded "${url}" into panel "${panel}"`);
767→  }
768→  
769→  document.querySelectorAll(".dual-aware a[href^='/'], a.dual-aware[href^='/']").forEach(link => {
770→    link.addEventListener("click", function (e) {
771→      e.preventDefault();
772→      const path = this.getAttribute("href");
773→      loadInPanel(getTargetPanel(), path);
774→    });
775→  });
776→
777→  const idForm = document.getElementById("idsearchForm");
778→  if (idForm) {
779→    idForm.addEventListener("submit", function (e) {
780→      e.preventDefault();
781→      const input = idForm.querySelector(".idsearchTerm").value.trim();
782→      if (!input) return;
783→
784→      let path = null;
785→
786→      if (/^\d+$/.test(input)) {
787→        path = "/go_to/" + encodeURIComponent(input);
788→      } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/i.test(input)) {
789→        const parts = input.split(/\s+/).filter(Boolean);
790→        path = "/range/" + parts.map(encodeURIComponent).join("/");
791→      } else {
792→        alert("Invalid input format.");
793→        return;
794→      }
795→
796→      loadInPanel(getTargetPanel(), path);
797→    });
798→  }
799→
800→  const searchForm = document.getElementById("searchForm");
801→  if (searchForm) {
802→    searchForm.addEventListener("submit", function (e) {
803→      e.preventDefault();
804→      const input = searchForm.querySelector(".searchTerm").value.trim();
805→      if (!input) return;
806→
807→      const path = "/search/" + encodeURIComponent(input);
808→      loadInPanel(getTargetPanel(), path);
809→    });
810→  }
811→});
812→</script>
