763→    const iframe = document.getElementById(`iframe-${panel}`);
764→    if (!iframe) return;
765→    const url = path.includes("?") ? path + "&embed=1" : path + "?embed=1";
766→    iframe.src = url;
767→    const inputBox = document.querySelector(`#panel-${panel} input`);
768→    if (inputBox) inputBox.value = path;
769→    console.log(`Loaded "${url}" into panel "${panel}"`);
