   220→document.addEventListener("DOMContentLoaded", function () {
   221→  document.body.addEventListener("click", function (e) {
   222→    const link = e.target.closest(".problem-reaction-link");
   223→    if (!link) return;
   224→
   225→    e.preventDefault();
   226→
   227→    const row = link.closest(".problem-reaction-row");
   228→    if (!row) return;
   229→
   230→    const form = row.querySelector(".problem-reaction-form");
   231→    if (!form) return;
   232→
   233→    const formData = new FormData(form);
   234→
   235→    fetch(form.action, {
   236→      method: "POST",
   237→      body: formData,
   238→      headers: {
   239→        "X-Requested-With": "XMLHttpRequest"
   240→      }
   241→    })
   242→      .then(resp => resp.json())
   243→      .then(data => {
   244→        if (!data.ok) {
   245→          console.error(data.error || "Problem reaction update failed");
   246→          return;
   247→        }
   248→
   249→        const summary = data.summary;
   250→
   251→        // Update users text for each reaction type
   252→        for (const [type, info] of Object.entries(summary)) {
   253→          const r = document.querySelector(
   254→            `.problem-reaction-row[data-reaction-type="${type}"]`
   255→          );
   256→          if (!r) continue;
   257→
   258→          const usersCell = r.querySelector(".problem-reaction-users");
   259→          if (usersCell) {
   260→            usersCell.textContent = info.users_title || "None yet";
   261→          }
   262→        }
   263→        link.classList.toggle("active");
   264→        if (link.classList.contains("active")) {
   265→          link.textContent = "(no)";
   266→        } else {
   267→          link.textContent = "(yes)";
   268→        }
   269→      })
   270→      .catch(err => {
   271→        console.error("Problem reaction AJAX error", err);
   272→      });
   273→  });
   274→});
   275→</script>
