635→<!-- Previewing forum posts -->
636→<script>
637→document.addEventListener("DOMContentLoaded", function () {
638→  function escapeHtml(str) {
639→    return str.replace(/[&<>"']/g, m => ({
640→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
641→    })[m]);
642→  }
643→  function normalizeHref(href){
644→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
645→      href = 'https://' + href;
646→    }
647→    return href;
648→  }
649→
650→  function renderPreview(textarea, preview, label) {
651→    let raw = textarea.value;
652→    const anchors = [];
653→    let idx = 0;
654→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
655→      (match, qHref, labelText) => {
656→        const href = qHref.slice(1, -1);
657→        const token = `__A_TAG_${idx++}__`;
658→        anchors.push({ token, href: normalizeHref(href), label: labelText });
659→        return token;
660→      });
661→    let safe = escapeHtml(raw);
662→    anchors.forEach(a => {
663→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
664→      safe = safe.replace(a.token, html);
665→    });
666→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
667→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
668→    );
669→    safe = safe.replace(/\n/g, "<br>");
670→    if (!safe.trim()) {
671→      preview.classList.add("hidden");
672→      label.classList.add("hidden");
673→      return;
674→    }
675→    preview.innerHTML = safe;
676→    preview.classList.remove("hidden");
677→    label.classList.remove("hidden");
678→    if (window.MathJax && MathJax.Hub) {
679→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
680→    }
681→  }
682→  document.querySelectorAll("form textarea").forEach(textarea => {
683→    const form = textarea.closest("form");
684→    const preview = form && form.querySelector(".post-preview");
685→    const label = form && form.querySelector(".preview-label");
686→    if (preview && label) {
687→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
688→      renderPreview(textarea, preview, label);
689→    }
690→  });
691→});
692→</script>
