643→<!-- Previewing forum posts -->
644→<script>
645→document.addEventListener("DOMContentLoaded", function () {
646→  function escapeHtml(str) {
647→    return str.replace(/[&<>"']/g, m => ({
648→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
649→    })[m]);
650→  }
651→  function normalizeHref(href){
652→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
653→      href = 'https://' + href;
654→    }
655→    return href;
656→  }
657→
658→  function renderPreview(textarea, preview, label) {
659→    let raw = textarea.value;
660→    const anchors = [];
661→    let idx = 0;
662→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
663→      (match, qHref, labelText) => {
664→        const href = qHref.slice(1, -1);
665→        const token = `__A_TAG_${idx++}__`;
666→        anchors.push({ token, href: normalizeHref(href), label: labelText });
667→        return token;
668→      });
669→    let safe = escapeHtml(raw);
670→    anchors.forEach(a => {
671→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
672→      safe = safe.replace(a.token, html);
673→    });
674→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
675→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
676→    );
677→    safe = safe.replace(/\n/g, "<br>");
678→    if (!safe.trim()) {
679→      preview.classList.add("hidden");
680→      label.classList.add("hidden");
681→      return;
682→    }
683→    preview.innerHTML = safe;
684→    preview.classList.remove("hidden");
685→    label.classList.remove("hidden");
686→    if (window.MathJax && MathJax.Hub) {
687→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
688→    }
689→  }
690→  document.querySelectorAll("form textarea").forEach(textarea => {
691→    const form = textarea.closest("form");
692→    const preview = form && form.querySelector(".post-preview");
693→    const label = form && form.querySelector(".preview-label");
694→    if (preview && label) {
695→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
696→      renderPreview(textarea, preview, label);
697→    }
698→  });
699→});
700→</script>
