698→<!-- To handle the search bar -->
699→<script>
700→document.addEventListener('DOMContentLoaded', function () {
701→    function setFilterLink(linkId, newFilter) {
702→        const link = document.getElementById(linkId);
703→        if (!link) return;
704→        const pathParts = window.location.pathname.split("/");
705→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
706→        if (pathParts.length >= 3) {
707→            const section = pathParts[1];
708→            const entry = pathParts[2];
709→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
710→            const baseUrl = "/" + section + "/" + entry;
711→            link.href = baseUrl + "/" + newFilter;
712→        }
713→    }
714→    setFilterLink("solvedLink", "solved");
715→    setFilterLink("openLink", "open");
716→    setFilterLink("yesLink", "yes");
717→    setFilterLink("noLink", "no");
