721→<!-- Handle the dual view link -->
722→<script>
723→document.addEventListener("DOMContentLoaded", function () {
724→  const dualLink = document.getElementById("dualViewLink");
725→  if (!dualLink) return;
726→
727→  const isInDualView = window.location.pathname.startsWith("/dual");
728→  dualLink.textContent = isInDualView ? "Exit Dual View" : "Dual View";
729→  dualLink.addEventListener("click", function (e) {
730→    e.preventDefault();
731→
732→    if (isInDualView) {
733→      try {
734→        const leftIframe = document.getElementById("iframe-left");
735→        const src = leftIframe?.src || "/";
736→        let clean = src.replace(window.location.origin, "");
737→        clean = clean.replace(/[?&]embed=1/, "");
738→
739→        window.location.href = clean;
740→      } catch (err) {
741→        window.location.href = "/";
742→      }
743→    } else {
744→      const currentPath = window.location.pathname + window.location.search;
745→      const rightPanelDefault = "/";
746→      const hash = `#left:${encodeURIComponent(currentPath)}&right:${encodeURIComponent(rightPanelDefault)}`;
747→      window.location.href = `/dual${hash}`;
748→    }
749→  });
750→});
