657→<!-- Previewing forum posts -->
658→<script>
659→document.addEventListener("DOMContentLoaded", function () {
660→  function escapeHtml(str) {
661→    return str.replace(/[&<>"']/g, m => ({
662→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
663→    })[m]);
664→  }
665→  function normalizeHref(href){
666→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
667→      href = 'https://' + href;
668→    }
669→    return href;
670→  }
671→
672→  function renderPreview(textarea, preview, label) {
673→    let raw = textarea.value;
674→    const anchors = [];
675→    let idx = 0;
676→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
677→      (match, qHref, labelText) => {
678→        const href = qHref.slice(1, -1);
679→        const token = `__A_TAG_${idx++}__`;
680→        anchors.push({ token, href: normalizeHref(href), label: labelText });
681→        return token;
682→      });
683→    let safe = escapeHtml(raw);
684→    anchors.forEach(a => {
685→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
686→      safe = safe.replace(a.token, html);
687→    });
688→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
689→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
690→    );
691→    safe = safe.replace(/\n/g, "<br>");
692→    if (!safe.trim()) {
693→      preview.classList.add("hidden");
694→      label.classList.add("hidden");
695→      return;
696→    }
697→    preview.innerHTML = safe;
698→    preview.classList.remove("hidden");
699→    label.classList.remove("hidden");
700→    if (window.MathJax && MathJax.Hub) {
701→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
702→    }
703→  }
704→  document.querySelectorAll("form textarea").forEach(textarea => {
705→    const form = textarea.closest("form");
706→    const preview = form && form.querySelector(".post-preview");
707→    const label = form && form.querySelector(".preview-label");
708→    if (preview && label) {
709→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
710→      renderPreview(textarea, preview, label);
711→    }
712→  });
713→});
714→</script>
