143→<script>
144→document.addEventListener('DOMContentLoaded', function() {
145→  if (window.location.pathname.startsWith("/dual")) return;
146→
147→  var form = document.getElementById('idsearchForm');
148→  if (!form) return;
149→
150→  form.onsubmit = function() {
151→    var input = document.querySelector('.idsearchTerm').value.trim();
152→    var searchUrl;
153→
154→    if (/^\d+$/.test(input)) {
155→        searchUrl = '/' + encodeURIComponent(input);
156→    } else if (/^((\d+|end)(-(\d+|end))?\s*)+(open|solved|yes|no)?$/.test(input.toLowerCase())) {
157→        let parts = input.split(/\s+/).filter(Boolean);
158→        searchUrl = '/range/' + parts.map(encodeURIComponent).join('/');
159→    } else {
160→        alert("Enter either a single integer or a space-separated list of numbers/ranges...");
161→        return false;
162→    }
163→    window.location.href = searchUrl;
164→    return false;
165→  };
166→});
