626→      "&":"&amp;","<":"&lt;",">":"&gt;",'"':"&quot;","'":"&#39;"
627→    })[m]);
628→  }
629→  function normalizeHref(href){
630→    if (!/^https?:\/\/?/i.test(href) && !href.startsWith('/')) {
631→      href = 'https://' + href;
632→    }
633→    return href;
634→  }
635→
636→  function renderPreview(textarea, preview, label) {
637→    let raw = textarea.value;
638→    const anchors = [];
639→    let idx = 0;
640→    raw = raw.replace(/<a\s+href=("(?:[^"]*)"|'(?:[^']*)')\s*>([\s\S]*?)<\/a>/gi,
641→      (match, qHref, labelText) => {
642→        const href = qHref.slice(1, -1);
643→        const token = `__A_TAG_${idx++}__`;
644→        anchors.push({ token, href: normalizeHref(href), label: labelText });
645→        return token;
646→      });
647→    let safe = escapeHtml(raw);
648→    anchors.forEach(a => {
649→      const html = `<a href="${escapeHtml(a.href)}">${escapeHtml(a.label)}</a>`;
650→      safe = safe.replace(a.token, html);
651→    });
652→    safe = safe.replace(/\{PROBLEM=(.+?)\}/g, (_, p1) =>
653→      `<a href="/${escapeHtml(p1)}">[${escapeHtml(p1)}]</a>`
654→    );
655→    safe = safe.replace(/\n/g, "<br>");
656→    if (!safe.trim()) {
657→      preview.classList.add("hidden");
658→      label.classList.add("hidden");
659→      return;
660→    }
661→    preview.innerHTML = safe;
662→    preview.classList.remove("hidden");
663→    label.classList.remove("hidden");
664→    if (window.MathJax && MathJax.Hub) {
665→      MathJax.Hub.Queue(["Typeset", MathJax.Hub, preview]);
666→    }
667→  }
668→  document.querySelectorAll("form textarea").forEach(textarea => {
669→    const form = textarea.closest("form");
670→    const preview = form && form.querySelector(".post-preview");
671→    const label = form && form.querySelector(".preview-label");
672→    if (preview && label) {
673→      textarea.addEventListener("input", () => renderPreview(textarea, preview, label));
674→      renderPreview(textarea, preview, label);
675→    }
676→  });
677→});
678→</script>
