680→<!-- To handle the search bar -->
681→<script>
682→document.addEventListener('DOMContentLoaded', function () {
683→    function setFilterLink(linkId, newFilter) {
684→        const link = document.getElementById(linkId);
685→        if (!link) return;
686→        const pathParts = window.location.pathname.split("/");
687→        // Expect: /<section>/<entry> or /<section>/<entry>/<filter>
688→        if (pathParts.length >= 3) {
689→            const section = pathParts[1];
690→            const entry = pathParts[2];
691→            // Construct base: /tags/additive, /sources_bib/BL09, etc.
692→            const baseUrl = "/" + section + "/" + entry;
693→            link.href = baseUrl + "/" + newFilter;
694→        }
695→    }
696→    setFilterLink("solvedLink", "solved");
697→    setFilterLink("openLink", "open");
698→    setFilterLink("yesLink", "yes");
699→    setFilterLink("noLink", "no");
